INSERT INTO `n_test` VALUES (0000714788, '古希臘文學', '古希臘文學是指古代希臘世界的文學。廣義的古希臘文學涵蓋了從氏族制希臘社會到希臘化時代的文學，持續時間近1000年。古希臘文學是整個西方文學的源頭，也是歐洲文學的第一個高峰。\r\n\r\n古希臘文學反映了歐洲從氏族社會向奴隸制社會過渡時期的現實生活，特別體現了古代世界的人們對戰爭與和平、人與自然之間的關係的思考。古希臘時代顯赫的英雄行為和社會歷史的重大變遷都在文學作品中得以深刻的體現。這些文學作品不僅為整個西方文學的發展奠定了基調，也為人們研究古希臘世界的歷史與社會提供了豐富的文獻資料。', '&#21476;&#24076;&#33240;&#25991;&#23416;', '&#21476;&#24076;&#33240;&#25991;&#23416;&#26159;&#25351;&#21476;&#20195;&#24076;&#33240;&#19990;&#30028;&#30340;&#25991;&#23416;&#12290;&#24291;&#32681;&#30340;&#21476;&#24076;&#33240;&#25991;&#23416;&#28085;&#33995;&#20102;&#24478;&#27663;&#26063;&#21046;&#24076;&#33240;&#31038;&#26371;&#21040;&#24076;&#33240;&#21270;&#26178;&#20195;&#30340;&#25991;&#23416;&#65292;&#25345;&#32396;&#26178;&#38291;&#36817;1000&#24180;&#12290;&#21476;&#24076;&#33240;&#25991;&#23416;&#26159;&#25972;&#20491;&#35199;&#26041;&#25991;&#23416;&#30340;&#28304;&#38957;&#65292;&#20063;&#26159;&#27472;&#27954;&#25991;&#23416;&#30340;&#31532;&#19968;&#20491;&#39640;&#23792;&#12290;\r\n\r\n&#21476;&#24076;&#33240;&#25991;&#23416;&#21453;&#26144;&#20102;&#27472;&#27954;&#24478;&#27663;&#26063;&#31038;&#26371;&#21521;&#22900;&#38584;&#21046;&#31038;&#26371;&#36942;&#28193;&#26178;&#26399;&#30340;&#29694;&#23526;&#29983;&#27963;&#65292;&#29305;&#21029;&#39636;&#29694;&#20102;&#21476;&#20195;&#19990;&#30028;&#30340;&#20154;&#20497;&#23565;&#25136;&#29229;&#33287;&#21644;&#24179;&#12289;&#20154;&#33287;&#33258;&#28982;&#20043;&#38291;&#30340;&#38364;&#20418;&#30340;&#24605;&#32771;&#12290;&#21476;&#24076;&#33240;&#26178;&#20195;&#39023;&#36203;&#30340;&#33521;&#38596;&#34892;&#28858;&#21644;&#31038;&#26371;&#27511;&#21490;&#30340;&#37325;&#22823;&#35722;&#36983;&#37117;&#22312;&#25991;&#23416;&#20316;&#21697;&#20013;&#24471;&#20197;&#28145;&#21051;&#30340;&#39636;&#29694;&#12290;&#36889;&#20123;&#25991;&#23416;&#20316;&#21697;&#19981;&#20677;&#28858;&#25972;&#20491;&#35199;&#26041;&#25991;&#23416;&#30340;&#30332;&#23637;&#22880;&#23450;&#20102;&#22522;&#35519;&#65292;&#20063;&#28858;&#20154;&#20497;&#30740;&#31350;&#21476;&#24076;&#33240;&#19990;&#30028;&#30340;&#27511;&#21490;&#33287;&#31038;&#26371;&#25552;&#20379;&#20102;&#35920;&#23500;&#30340;&#25991;&#29563;&#36039;&#26009;&#12290;');
INSERT INTO `n_test` VALUES (0000714789, '古羅馬文學', '古羅馬文學指紀元前後繁榮於古羅馬政權（包括羅馬共和國和羅馬帝國）治下的文學。其主要語言是拉丁語。儘管古羅馬共和國誕生於公元前510年（擺脫伊特魯利亞王朝的統治），但按照慣例，真正意義上的古羅馬文學則從公元前240年算起。', '&#21476;&#32645;&#39340;&#25991;&#23416;', '&#21476;&#32645;&#39340;&#25991;&#23416;&#25351;&#32000;&#20803;&#21069;&#24460;&#32321;&#27054;&#26044;&#21476;&#32645;&#39340;&#25919;&#27402;&#65288;&#21253;&#25324;&#32645;&#39340;&#20849;&#21644;&#22283;&#21644;&#32645;&#39340;&#24093;&#22283;&#65289;&#27835;&#19979;&#30340;&#25991;&#23416;&#12290;&#20854;&#20027;&#35201;&#35486;&#35328;&#26159;&#25289;&#19969;&#35486;&#12290;&#20760;&#31649;&#21476;&#32645;&#39340;&#20849;&#21644;&#22283;&#35477;&#29983;&#26044;&#20844;&#20803;&#21069;510&#24180;&#65288;&#25850;&#33067;&#20234;&#29305;&#39791;&#21033;&#20126;&#29579;&#26397;&#30340;&#32113;&#27835;&#65289;&#65292;&#20294;&#25353;&#29031;&#24931;&#20363;&#65292;&#30495;&#27491;&#24847;&#32681;&#19978;&#30340;&#21476;&#32645;&#39340;&#25991;&#23416;&#21063;&#24478;&#20844;&#20803;&#21069;240&#24180;&#31639;&#36215;&#12290;');
INSERT INTO `n_test` VALUES (0000714790, '中世紀文學', '中世紀文學是指歐洲各國中世紀時期的文學。在史學上，一般認為中世紀是指從公元450年左右羅馬帝國衰亡到15世紀文藝復興之間約1000年的時間。其思想文化上的主要特徵是天主教會的勢力強大，基督教神學影響到包括文學、藝術、音樂在內的一切藝術形式。\r\n\r\n西方學術界對中世紀文學的認識和評價經歷了一個轉變的過程。在19世紀以前，人們普遍認為中世紀文學是輝煌的古希臘古羅馬文學和發端於義大利的文藝復興文學之間的低谷。這期間經濟停滯、倒退，政治反動，蒙昧主義猖獗，文學成為神學的附庸。但19世紀早期的浪漫派作家們在中世紀哥德文化中看到了理想主義、英雄主義、對精神的崇尚和對女性的讚頌，中世紀文學得以重新發掘，並被認為是西方文學史上最重要的流派。', '&#20013;&#19990;&#32000;&#25991;&#23416;', '&#20013;&#19990;&#32000;&#25991;&#23416;&#26159;&#25351;&#27472;&#27954;&#21508;&#22283;&#20013;&#19990;&#32000;&#26178;&#26399;&#30340;&#25991;&#23416;&#12290;&#22312;&#21490;&#23416;&#19978;&#65292;&#19968;&#33324;&#35469;&#28858;&#20013;&#19990;&#32000;&#26159;&#25351;&#24478;&#20844;&#20803;450&#24180;&#24038;&#21491;&#32645;&#39340;&#24093;&#22283;&#34928;&#20129;&#21040;15&#19990;&#32000;&#25991;&#34269;&#24489;&#33288;&#20043;&#38291;&#32004;1000&#24180;&#30340;&#26178;&#38291;&#12290;&#20854;&#24605;&#24819;&#25991;&#21270;&#19978;&#30340;&#20027;&#35201;&#29305;&#24501;&#26159;&#22825;&#20027;&#25945;&#26371;&#30340;&#21218;&#21147;&#24375;&#22823;&#65292;&#22522;&#30563;&#25945;&#31070;&#23416;&#24433;&#38911;&#21040;&#21253;&#25324;&#25991;&#23416;&#12289;&#34269;&#34899;&#12289;&#38899;&#27138;&#22312;&#20839;&#30340;&#19968;&#20999;&#34269;&#34899;&#24418;&#24335;&#12290;\r\n\r\n&#35199;&#26041;&#23416;&#34899;&#30028;&#23565;&#20013;&#19990;&#32000;&#25991;&#23416;&#30340;&#35469;&#35672;&#21644;&#35413;&#20729;&#32147;&#27511;&#20102;&#19968;&#20491;&#36681;&#35722;&#30340;&#36942;&#31243;&#12290;&#22312;19&#19990;&#32000;&#20197;&#21069;&#65292;&#20154;&#20497;&#26222;&#36941;&#35469;&#28858;&#20013;&#19990;&#32000;&#25991;&#23416;&#26159;&#36637;&#29004;&#30340;&#21476;&#24076;&#33240;&#21476;&#32645;&#39340;&#25991;&#23416;&#21644;&#30332;&#31471;&#26044;&#32681;&#22823;&#21033;&#30340;&#25991;&#34269;&#24489;&#33288;&#25991;&#23416;&#20043;&#38291;&#30340;&#20302;&#35895;&#12290;&#36889;&#26399;&#38291;&#32147;&#28639;&#20572;&#28399;&#12289;&#20498;&#36864;&#65292;&#25919;&#27835;&#21453;&#21205;&#65292;&#33945;&#26151;&#20027;&#32681;&#29462;&#29527;&#65292;&#25991;&#23416;&#25104;&#28858;&#31070;&#23416;&#30340;&#38468;&#24248;&#12290;&#20294;19&#19990;&#32000;&#26089;&#26399;&#30340;&#28010;&#28459;&#27966;&#20316;&#23478;&#20497;&#22312;&#20013;&#19990;&#32000;&#21733;&#24503;&#25991;&#21270;&#20013;&#30475;&#21040;&#20102;&#29702;&#24819;&#20027;&#32681;&#12289;&#33521;&#38596;&#20027;&#32681;&#12289;&#23565;&#31934;&#31070;&#30340;&#23815;&#23578;&#21644;&#23565;&#22899;&#24615;&#30340;&#35738;&#38924;&#65292;&#20013;&#19990;&#32000;&#25991;&#23416;&#24471;&#20197;&#37325;&#26032;&#30332;&#25496;&#65292;&#20006;&#34987;&#35469;&#28858;&#26159;&#35199;&#26041;&#25991;&#23416;&#21490;&#19978;&#26368;&#37325;&#35201;&#30340;&#27969;&#27966;&#12290;');
INSERT INTO `n_test` VALUES (0000714791, ' 古典主義', '古典主義（英語：Classicism）在藝術上主要是指對包含古希臘及古羅馬的古典時代文化的高度認同。古典主義將古典時代的品味作為標準，並試圖模仿其風格。古典主義出現於中世紀後的歐洲，是啟蒙時代、理性時代以及部分現代主義思想所提倡的概念。', ' &#21476;&#20856;&#20027;&#32681;', '&#21476;&#20856;&#20027;&#32681;&#65288;&#33521;&#35486;&#65306;Classicism&#65289;&#22312;&#34269;&#34899;&#19978;&#20027;&#35201;&#26159;&#25351;&#23565;&#21253;&#21547;&#21476;&#24076;&#33240;&#21450;&#21476;&#32645;&#39340;&#30340;&#21476;&#20856;&#26178;&#20195;&#25991;&#21270;&#30340;&#39640;&#24230;&#35469;&#21516;&#12290;&#21476;&#20856;&#20027;&#32681;&#23559;&#21476;&#20856;&#26178;&#20195;&#30340;&#21697;&#21619;&#20316;&#28858;&#27161;&#28310;&#65292;&#20006;&#35430;&#22294;&#27169;&#20223;&#20854;&#39080;&#26684;&#12290;&#21476;&#20856;&#20027;&#32681;&#20986;&#29694;&#26044;&#20013;&#19990;&#32000;&#24460;&#30340;&#27472;&#27954;&#65292;&#26159;&#21855;&#33945;&#26178;&#20195;&#12289;&#29702;&#24615;&#26178;&#20195;&#20197;&#21450;&#37096;&#20998;&#29694;&#20195;&#20027;&#32681;&#24605;&#24819;&#25152;&#25552;&#20513;&#30340;&#27010;&#24565;&#12290;');
INSERT INTO `n_test` VALUES (0000714792, ' 巴洛克文學', '巴洛克文學是17世紀義大利唯一有影響的文學流派，文風華麗浮誇，強調運動和變化，側重表現幻想，因而特別有裝飾美和形式美。巴洛克作家致力於描繪一種「烏托邦」式的世界，其作品中充滿了天主教的思想。文學主題是「人生如夢」，提出了「人生舞台」的概念，認為世界是一個大舞台，根本沒有現實，人也不可能認識現實。巴洛克文學在德國義大利西班牙都很有影響， 代表作家及作品有卡爾德隆的小說《人生如夢》、格里美爾·斯豪森的《痴兒西木傳》等。', ' &#24052;&#27931;&#20811;&#25991;&#23416;', '&#24052;&#27931;&#20811;&#25991;&#23416;&#26159;17&#19990;&#32000;&#32681;&#22823;&#21033;&#21807;&#19968;&#26377;&#24433;&#38911;&#30340;&#25991;&#23416;&#27969;&#27966;&#65292;&#25991;&#39080;&#33775;&#40599;&#28014;&#35463;&#65292;&#24375;&#35519;&#36939;&#21205;&#21644;&#35722;&#21270;&#65292;&#20596;&#37325;&#34920;&#29694;&#24187;&#24819;&#65292;&#22240;&#32780;&#29305;&#21029;&#26377;&#35037;&#39166;&#32654;&#21644;&#24418;&#24335;&#32654;&#12290;&#24052;&#27931;&#20811;&#20316;&#23478;&#33268;&#21147;&#26044;&#25551;&#32362;&#19968;&#31278;&#12300;&#28879;&#25176;&#37030;&#12301;&#24335;&#30340;&#19990;&#30028;&#65292;&#20854;&#20316;&#21697;&#20013;&#20805;&#28415;&#20102;&#22825;&#20027;&#25945;&#30340;&#24605;&#24819;&#12290;&#25991;&#23416;&#20027;&#38988;&#26159;&#12300;&#20154;&#29983;&#22914;&#22818;&#12301;&#65292;&#25552;&#20986;&#20102;&#12300;&#20154;&#29983;&#33310;&#21488;&#12301;&#30340;&#27010;&#24565;&#65292;&#35469;&#28858;&#19990;&#30028;&#26159;&#19968;&#20491;&#22823;&#33310;&#21488;&#65292;&#26681;&#26412;&#27794;&#26377;&#29694;&#23526;&#65292;&#20154;&#20063;&#19981;&#21487;&#33021;&#35469;&#35672;&#29694;&#23526;&#12290;&#24052;&#27931;&#20811;&#25991;&#23416;&#22312;&#24503;&#22283;&#32681;&#22823;&#21033;&#35199;&#29677;&#29273;&#37117;&#24456;&#26377;&#24433;&#38911;&#65292; &#20195;&#34920;&#20316;&#23478;&#21450;&#20316;&#21697;&#26377;&#21345;&#29246;&#24503;&#38534;&#30340;&#23567;&#35498;&#12298;&#20154;&#29983;&#22914;&#22818;&#12299;&#12289;&#26684;&#37324;&#32654;&#29246;&middot;&#26031;&#35946;&#26862;&#30340;&#12298;&#30196;&#20818;&#35199;&#26408;&#20659;&#12299;&#31561;&#12290;');
INSERT INTO `n_test` VALUES (0000714793, ' 啟蒙文學', '啟蒙文學是指盛行於18世紀歐洲啟蒙運動時期的文學。從嚴格的意義上講，「啟蒙文學」並不是如浪漫主義文學或自然主義文學一樣的嚴格意義上的文學流派，而是席捲整個歐洲的啟蒙思想在文學上的延伸和體現。啟蒙文學並沒有獨立的綱領，在文學史上對這一流派的界定也存在很多爭議，但通常認為奧古斯都時期的英國文學、大革命之前的法國文學以及德國的狂飆突進運動，都可以算作啟蒙文學範疇。\r\n\r\n從縱向上看，啟蒙文學選擇性繼承了17世紀法國古典主義文學的某些特性，但是卻已經具備了近代文學的諸多元素，起到了承上啟下的重要作用。儘管沒有形成統一的綱領，各國發展的狀況也存在差距，但仍取得了相當輝煌的成就。歐洲諸國中以法國成就最高，英國和德國次之，在俄國和義大利等國也有相應的 發展。', ' &#21855;&#33945;&#25991;&#23416;', '&#21855;&#33945;&#25991;&#23416;&#26159;&#25351;&#30427;&#34892;&#26044;18&#19990;&#32000;&#27472;&#27954;&#21855;&#33945;&#36939;&#21205;&#26178;&#26399;&#30340;&#25991;&#23416;&#12290;&#24478;&#22196;&#26684;&#30340;&#24847;&#32681;&#19978;&#35611;&#65292;&#12300;&#21855;&#33945;&#25991;&#23416;&#12301;&#20006;&#19981;&#26159;&#22914;&#28010;&#28459;&#20027;&#32681;&#25991;&#23416;&#25110;&#33258;&#28982;&#20027;&#32681;&#25991;&#23416;&#19968;&#27171;&#30340;&#22196;&#26684;&#24847;&#32681;&#19978;&#30340;&#25991;&#23416;&#27969;&#27966;&#65292;&#32780;&#26159;&#24109;&#25458;&#25972;&#20491;&#27472;&#27954;&#30340;&#21855;&#33945;&#24605;&#24819;&#22312;&#25991;&#23416;&#19978;&#30340;&#24310;&#20280;&#21644;&#39636;&#29694;&#12290;&#21855;&#33945;&#25991;&#23416;&#20006;&#27794;&#26377;&#29544;&#31435;&#30340;&#32177;&#38936;&#65292;&#22312;&#25991;&#23416;&#21490;&#19978;&#23565;&#36889;&#19968;&#27969;&#27966;&#30340;&#30028;&#23450;&#20063;&#23384;&#22312;&#24456;&#22810;&#29229;&#35696;&#65292;&#20294;&#36890;&#24120;&#35469;&#28858;&#22887;&#21476;&#26031;&#37117;&#26178;&#26399;&#30340;&#33521;&#22283;&#25991;&#23416;&#12289;&#22823;&#38761;&#21629;&#20043;&#21069;&#30340;&#27861;&#22283;&#25991;&#23416;&#20197;&#21450;&#24503;&#22283;&#30340;&#29378;&#39110;&#31361;&#36914;&#36939;&#21205;&#65292;&#37117;&#21487;&#20197;&#31639;&#20316;&#21855;&#33945;&#25991;&#23416;&#31684;&#30087;&#12290;\r\n\r\n&#24478;&#32305;&#21521;&#19978;&#30475;&#65292;&#21855;&#33945;&#25991;&#23416;&#36984;&#25799;&#24615;&#32380;&#25215;&#20102;17&#19990;&#32000;&#27861;&#22283;&#21476;&#20856;&#20027;&#32681;&#25991;&#23416;&#30340;&#26576;&#20123;&#29305;&#24615;&#65292;&#20294;&#26159;&#21371;&#24050;&#32147;&#20855;&#20633;&#20102;&#36817;&#20195;&#25991;&#23416;&#30340;&#35576;&#22810;&#20803;&#32032;&#65292;&#36215;&#21040;&#20102;&#25215;&#19978;&#21855;&#19979;&#30340;&#37325;&#35201;&#20316;&#29992;&#12290;&#20760;&#31649;&#27794;&#26377;&#24418;&#25104;&#32113;&#19968;&#30340;&#32177;&#38936;&#65292;&#21508;&#22283;&#30332;&#23637;&#30340;&#29376;&#27841;&#20063;&#23384;&#22312;&#24046;&#36317;&#65292;&#20294;&#20173;&#21462;&#24471;&#20102;&#30456;&#30070;&#36637;&#29004;&#30340;&#25104;&#23601;&#12290;&#27472;&#27954;&#35576;&#22283;&#20013;&#20197;&#27861;&#22283;&#25104;&#23601;&#26368;&#39640;&#65292;&#33521;&#22283;&#21644;&#24503;&#22283;&#27425;&#20043;&#65292;&#22312;&#20420;&#22283;&#21644;&#32681;&#22823;&#21033;&#31561;&#22283;&#20063;&#26377;&#30456;&#25033;&#30340; &#30332;&#23637;&#12290;');
INSERT INTO `n_test` VALUES (0000714794, ' 象徵主義文學', '象徵主義文學是起源於19世紀中葉的法國，並於20世紀初期擴及歐美各國的一個文學流派，是象徵主義思潮在文學上的體現，也是現代主義文學的一個核心分支，主要涵蓋詩歌和戲劇兩大領域，其影響力一直持續到今天。西方主流學術界認為象徵主義文學的誕生是古典文學和現代文學的分水嶺。\r\n\r\n法國詩人夏爾·波德萊爾和美國詩人愛倫·坡是象徵主義的先驅。儘管「象徵主義」一詞最早出現於1886年，但波德萊爾和愛倫·坡早在19世紀中葉的創作就最初涉及到了一些象徵主義的理念。', ' &#35937;&#24501;&#20027;&#32681;&#25991;&#23416;', '&#35937;&#24501;&#20027;&#32681;&#25991;&#23416;&#26159;&#36215;&#28304;&#26044;19&#19990;&#32000;&#20013;&#33865;&#30340;&#27861;&#22283;&#65292;&#20006;&#26044;20&#19990;&#32000;&#21021;&#26399;&#25844;&#21450;&#27472;&#32654;&#21508;&#22283;&#30340;&#19968;&#20491;&#25991;&#23416;&#27969;&#27966;&#65292;&#26159;&#35937;&#24501;&#20027;&#32681;&#24605;&#28526;&#22312;&#25991;&#23416;&#19978;&#30340;&#39636;&#29694;&#65292;&#20063;&#26159;&#29694;&#20195;&#20027;&#32681;&#25991;&#23416;&#30340;&#19968;&#20491;&#26680;&#24515;&#20998;&#25903;&#65292;&#20027;&#35201;&#28085;&#33995;&#35433;&#27468;&#21644;&#25138;&#21127;&#20841;&#22823;&#38936;&#22495;&#65292;&#20854;&#24433;&#38911;&#21147;&#19968;&#30452;&#25345;&#32396;&#21040;&#20170;&#22825;&#12290;&#35199;&#26041;&#20027;&#27969;&#23416;&#34899;&#30028;&#35469;&#28858;&#35937;&#24501;&#20027;&#32681;&#25991;&#23416;&#30340;&#35477;&#29983;&#26159;&#21476;&#20856;&#25991;&#23416;&#21644;&#29694;&#20195;&#25991;&#23416;&#30340;&#20998;&#27700;&#23994;&#12290;\r\n\r\n&#27861;&#22283;&#35433;&#20154;&#22799;&#29246;&middot;&#27874;&#24503;&#33802;&#29246;&#21644;&#32654;&#22283;&#35433;&#20154;&#24859;&#20523;&middot;&#22369;&#26159;&#35937;&#24501;&#20027;&#32681;&#30340;&#20808;&#39493;&#12290;&#20760;&#31649;&#12300;&#35937;&#24501;&#20027;&#32681;&#12301;&#19968;&#35422;&#26368;&#26089;&#20986;&#29694;&#26044;1886&#24180;&#65292;&#20294;&#27874;&#24503;&#33802;&#29246;&#21644;&#24859;&#20523;&middot;&#22369;&#26089;&#22312;19&#19990;&#32000;&#20013;&#33865;&#30340;&#21109;&#20316;&#23601;&#26368;&#21021;&#28041;&#21450;&#21040;&#20102;&#19968;&#20123;&#35937;&#24501;&#20027;&#32681;&#30340;&#29702;&#24565;&#12290;');
